mapper.select:
select province name,COUNT(province) value from orders
and hotel_id = #{hotelId}
group by province order by value desc

select ( case way when way = 1 then '到店' else '网单' end ) name,count(way) value from orders
and hotel_id = #{hotelId}
group by way

select name,count(name) value
from (
select DATE_FORMAT(create_time,'%Y-%m-%d') name
from orders
where DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(create_time)

    and hotel_id = #{hotelId}

)t
group by name
order by name

select name,count(name) value
from (
select DATE_FORMAT(create_time,'%Y-%m') name
from orders
where DATE_SUB(CURDATE(), INTERVAL 1 year) <= date(create_time)

    and hotel_id = #{hotelId}

)t
group by name
order by name

select name,sum(last_pay) value
from (
select DATE_FORMAT(create_time,'%Y-%m-%d') name,last_pay
from orders
where DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(create_time)

    and hotel_id = #{hotelId}

)t
group by name
order by name
