mapper.select:
select name,count(name) value
from (
select DATE_FORMAT(create_time,'%Y-%m-%d') name
from orders
where DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(create_time)

    and hotel_id = #{hotelId}

and is_deleted = 0
)t
group by name
order by name

select name,count(name) value
from (
select DATE_FORMAT(create_time,'%Y-%m') name
from orders
where DATE_SUB(CURDATE(), INTERVAL 1 year) <= date(create_time) and is_deleted = 0

    and hotel_id = #{hotelId}

)t
group by name
order by name

select name,sum(last_pay) value
from (
select DATE_FORMAT(create_time,'%Y-%m-%d') name,last_pay
from orders
where DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(create_time) and is_deleted = 0

    and hotel_id = #{hotelId}

)t
group by name
order by name

SELECT i.name,COUNT(i.way) value
from (
    SELECT
    DATE_FORMAT(check_in_time, '%Y-%m-%d') name,
    way
    FROM
    isolation_info
    where DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(check_in_time) and is_deleted = 0

        and hotel_id = #{hotelId}


        and way = #{way}

    ) i
GROUP BY
    i.name

SELECT province name,count(province) value from isolation_info where is_deleted = 0
and hotel_id = #{hotelId}
GROUP BY province
